mapper.select:
    select 
    
    from sc_promotional_product
    where 1=1
    
      and  storeId = #{product.storeid,jdbcType=VARCHAR}
      
      
     and   title = #{product.title,jdbcType=VARCHAR}
      
      
      and  photo = #{product.photo,jdbcType=VARCHAR}
      
      
      and  products = #{product.products,jdbcType=VARCHAR}
      
      
       and create_by = #{product.createBy,jdbcType=VARCHAR}
      
      
      and  create_time = #{product.createTime,jdbcType=TIMESTAMP}
      
      
      and  recommended_openid = #{product.recommendedOpenid,jdbcType=VARCHAR}
      
      
      and  promoter_id = #{product.promoterId,jdbcType=VARCHAR}
      
      
      and  recommended_unionid = #{product.recommendedUnionid,jdbcType=VARCHAR}
      
      
       and promoter_name = #{product.promoterName,jdbcType=VARCHAR}
      
      
       and checkAllTag = #{product.checkAllTag}
      
      
       and promoter_address = #{product.promoterAddress}
      
      
		LIMIT #{page.startRow},#{page.pageSize}
