mapper.select:
    select 
    
    from sc_store_addr
    where 1=1
	   
	    and type = #{type,jdbcType=INTEGER}
	   
       
       and contact = #{contact,jdbcType=VARCHAR}
      
      
       and phone = #{phone,jdbcType=VARCHAR}
      
      
       and address = #{address,jdbcType=VARCHAR}
      
      
       and isDefault = #{isdefault,jdbcType=INTEGER}
      
      
       and userid = #{userid,jdbcType=INTEGER}

mapper.insert:
insert into sc_store_addr (id, type, contact, 
  phone, address, isDefault,userid
  )
values (#{id,jdbcType=INTEGER}, #{type,jdbcType=INTEGER}, #{contact,jdbcType=VARCHAR}, 
  #{phone,jdbcType=VARCHAR}, #{address,jdbcType=VARCHAR}, #{isdefault,jdbcType=INTEGER},#{userid,jdbcType=INTEGER}
  )

mapper.update:
update sc_store_addr
