mapper.select:
select
id, storeId,
type, cate,
fastcode,
 image,
telephone,
points, 
remarkName, nickName,reallyName

from sp_client
where 1=1
and status ='0'

	and storeId = #{client.storeId,jdbcType=VARCHAR}


	and type = #{client.type,jdbcType=VARCHAR}


	and otherStoreId = #{client.otherStoreId,jdbcType=VARCHAR}


	and userId = #{client.userId,jdbcType=VARCHAR}


	and openid = #{client.openid,jdbcType=VARCHAR}


	and (remarkName like CONCAT('%',#{client.name},'%') or nickName like CONCAT('%',#{client.name},'%') or reallyName like CONCAT('%',#{client.name},'%'))


	and scene = #{client.scene,jdbcType=VARCHAR}


	and district in
	 #{item}


	and cate in
	 #{item}
	


	and id in
	 #{item}
	


	order by reallyName asc


	order by reallyName desc


	order by create_time asc


	order by create_time desc


	order by points asc


	order by points desc

ORDER BY createTime ASC

LIMIT #{page.startRow},#{page.pageSize}

select distinct(district)
from sp_client
where 1=1

	and storeId = #{client.storeId,jdbcType=VARCHAR}


	and type = #{client.type,jdbcType=VARCHAR}

and status ='0'
