mapper.select:
select 

from sp_employee
where 1=1
 
   and storeId = #{storeId,jdbcType=INTEGER}
  
 
  and id = #{id,jdbcType=INTEGER}
  
 
  and openid = #{openid,jdbcType=INTEGER}

mapper.delete:
delete from sp_employee
where storeId = #{storeId,jdbcType=INTEGER}
  
  and id = #{id,jdbcType=INTEGER}

mapper.update:
update sp_employee set status = #{status,jdbcType=INTEGER},
create_time = #{createTime,jdbcType=TIMESTAMP}, create_by = #{createBy,jdbcType=VARCHAR}, modify_time = #{modifyTime,jdbcType=TIMESTAMP}, modify_by = #{modifyBy,jdbcType=VARCHAR}, reallyName = #{reallyName,jdbcType=VARCHAR}, openid = #{openid,jdbcType=VARCHAR}, phoneBack = #{phoneBack,jdbcType=VARCHAR}, telephone = #{telephone,jdbcType=VARCHAR}, address = #{address,jdbcType=VARCHAR}, addressBack = #{addressBack,jdbcType=VARCHAR}, birthdate = #{birthdate,jdbcType=TIMESTAMP}, modifyPrice =#{modifyPrice,jdbcType=VARCHAR}, weChatNo =#{weChatNo,jdbcType=VARCHAR}, zeroPrice =#{zeroPrice,jdbcType=DECIMAL}, discount = #{discount,jdbcType=DECIMAL}, nickName = #{nickName,jdbcType=VARCHAR}
where storeId = #{storeId,jdbcType=INTEGER} and id = #{id,jdbcType=INTEGER}
