mapper.select:
SELECT sum(detail.count) as quantity, SUM(detail.price*detail.count) AS amount, detail.productId, detail.productName as productName, detail.productColor as productColor, detail.productSize as productSize, brand.brandName as brandName, grade.custTypeName as gradeName, store.name as storeName FROM ss_stodetail_1 detail LEFT JOIN ss_stoorder_#{storeId} sto ON sto.payOrderNo = detail.orderId left join sp_product_1 product on product.id=detail.productId left join sp_pbrand brand on brand.id = product.brandId and brand.storeId =detail.storeId left join sp_cust_prod_price grade on grade.type=1 and grade.storeId=detail.storeId and grade.custTypeCode = product.grade left join sp_store store on store.id = sto.sellerStoreId where 1=1 and sto.buyerStoreId=#{storeId} AND sto.orderTime >=str_to_date(#{timeFrom},'%Y-%m-%d %H:%i:%s') AND sto.orderTime <=str_to_date(#{timeTo},'%Y-%m-%d %H:%i:%s')
and ${filter.field} like CONCAT('%',#{filter.value},'%')
group by ${filter.field}

SELECT DISTINCT detail.productName as productName FROM ss_stodetail_#{query.storeId} detail LEFT JOIN ss_stoorder_#{query.storeId} sto ON sto.payOrderNo = detail.orderId left join sp_product_#{query.storeId} product on product.id=detail.productId left join sp_pbrand brand on brand.id = product.brandId and brand.storeId =detail.storeId LEFT JOIN sp_product_type productType ON productType.id =product.productType and productType.storeId =detail.storeId LEFT JOIN sp_client c ON c.storeId = sto.buyerStoreId LEFT JOIN sc_wechat_bind wechat ON wechat.openId = c.openid AND wechat.storeId = sto.sellerStoreId where 1=1 AND wechat.storeId IS NOT NULL AND sto.orderStatus ='5' and sto.buyerStoreId=#{query.storeId} AND sto.orderTime >=str_to_date(#{query.timeFrom},'%Y-%m-%d %H:%i:%s') AND sto.orderTime <=str_to_date(#{query.timeTo},'%Y-%m-%d %H:%i:%s')
and productName = #{query.productName}
and productType.cateName in #{item}
and brand.brandName in #{item}
and product.season in #{item}
and detail.productColor in #{item}
and detail.productSize in #{item}
and c.id in #{item}
and sto.operator in #{item}
LIMIT #{page.startRow},#{page.pageSize}

SELECT sum(detail.count) as quantity, #{query.timeFrom} as timeFrom, #{query.timeTo} as timeTo FROM ss_stodetail_#{query.storeId} detail LEFT JOIN ss_stoorder_#{query.storeId} sto ON sto.payOrderNo = detail.orderId where 1=1 AND sto.orderStatus ='5' and sto.buyerStoreId=#{query.storeId} AND sto.orderTime >=str_to_date(#{query.timeFrom},'%Y-%m-%d %H:%i:%s') AND sto.orderTime <=str_to_date(#{query.timeTo},'%Y-%m-%d %H:%i:%s')
and productName = #{query.productName}
union all SELECT sum(detail.count) as quantity, #{item.timeFrom} as timeFrom, #{item.timeTo} as timeTo FROM ss_stodetail_#{item.storeId} detail LEFT JOIN ss_stoorder_#{item.storeId} sto ON sto.payOrderNo = detail.orderId where 1=1 AND sto.orderStatus ='5' and sto.buyerStoreId=#{item.storeId} AND sto.orderTime >=str_to_date(#{item.timeFrom},'%Y-%m-%d %H:%i:%s') AND sto.orderTime <=str_to_date(#{item.timeTo},'%Y-%m-%d %H:%i:%s')
and productName = #{item.productName}
