mapper.select:
  select id, parentId, storePhoto, name,storeType, inventoryShare
  from sp_store
  where 1=1
  and status =0
   
     and parentId = #{store.parentid,jdbcType=INTEGER}
    
    
     and storePhoto = #{store.storephoto,jdbcType=VARCHAR}
    
    
     and storeType = #{store.storeType,jdbcType=INTEGER}
    
    
     and inventoryShare = #{store.inventoryShare,jdbcType=INTEGER}
    
    
     and name = #{store.name,jdbcType=VARCHAR}
    
    
	   and id = #{store.id,jdbcType=INTEGER}
    
    
	   and id in 
	    #{item}
    
    
    LIMIT #{page.startRow},#{page.pageSize}
