mapper.select:
select

from ss_stodetail
where orderId = #{orderNo,jdbcType=VARCHAR}

and storeId = #{storeId,jdbcType=VARCHAR}

SELECT SUM(detail.price*detail.count)/SUM(detail.count) as average, detail.productId, detail.productName AS productName FROM ss_stodetail detail LEFT JOIN ss_stoorder sto ON sto.payOrderNo = detail.orderId WHERE 1=1 and detail.productId is not null
AND sto.buyerStoreId=#{buyerstoreid}
AND detail.productId IN #{item}
GROUP BY productId

SELECT DISTINCT detail.productId as id, detail.productColor as colors, detail.count, detail.price, detail.productName as name, detail.productSize as sizes, detail.productUnit as unit, (CASE WHEN product.image IS NOT NULL THEN product.image WHEN product.image1 IS NOT NULL THEN product.image1 WHEN product.image2 IS NOT NULL THEN product.image2 WHEN product.image3 IS NOT NULL THEN product.image3 WHEN product.image4 IS NOT NULL THEN product.image4 ELSE product.image END) as image FROM ss_stodetail detail LEFT JOIN ss_stoorder sto ON sto.payOrderNo = detail.orderId LEFT JOIN sp_product product ON product.id = detail.productId and product.storeId = detail.storeId WHERE 1 = 1 AND sto.buyerStoreId = #{query.buyerstoreid} AND sto.sellerStoreId = #{query.sellerstoreid}
ORDER BY detail.createTime DESC
LIMIT #{page.startRow},#{page.pageSize}

mapper.insert:
insert into ss_stodetail (orderId, storeId, handtag, productId, productName, productUnit, productColor, productSize, count, price, createTime, createBy, modifyTime, modifyBy ) values ( #{item.orderid,jdbcType=VARCHAR}, #{item.storeid,jdbcType=VARCHAR}, #{item.handtag,jdbcType=INTEGER}, #{item.productid,jdbcType=VARCHAR}, #{item.productname,jdbcType=VARCHAR}, #{item.productunit,jdbcType=VARCHAR}, #{item.productcolor,jdbcType=VARCHAR}, #{item.productsize,jdbcType=VARCHAR}, #{item.count,jdbcType=DECIMAL}, #{item.price,jdbcType=DECIMAL}, #{item.createtime,jdbcType=TIMESTAMP}, #{item.createby,jdbcType=VARCHAR}, #{item.modifytime,jdbcType=TIMESTAMP}, #{item.modifyby,jdbcType=VARCHAR} )
;
insert into ss_member_stodetail (orderId, storeId, handtag, productId, productName, productUnit, productColor, productSize, count, price, createTime, createBy, modifyTime, modifyBy ) values ( #{item.orderid,jdbcType=VARCHAR}, #{sellerStoreId,jdbcType=VARCHAR}, #{item.handtag,jdbcType=INTEGER}, #{item.productid,jdbcType=VARCHAR}, #{item.productname,jdbcType=VARCHAR}, #{item.productunit,jdbcType=VARCHAR}, #{item.productcolor,jdbcType=VARCHAR}, #{item.productsize,jdbcType=VARCHAR}, #{item.count,jdbcType=DECIMAL}, #{item.price,jdbcType=DECIMAL}, #{item.createtime,jdbcType=TIMESTAMP}, #{item.createby,jdbcType=VARCHAR}, #{item.modifytime,jdbcType=TIMESTAMP}, #{item.modifyby,jdbcType=VARCHAR} )
;

update.foreach:
update ss_stodetail

	
		orderId = #{item.orderid,jdbcType=VARCHAR},
	
	
		storeId = #{item.storeid,jdbcType=VARCHAR},
	
	
		handtag = #{item.handtag,jdbcType=INTEGER},
	
	
		productId = #{item.productid,jdbcType=VARCHAR},
	
	
		productName = #{item.productname,jdbcType=VARCHAR},
	
	
		productUnit = #{item.productunit,jdbcType=VARCHAR},
	
	
		productColor = #{item.productcolor,jdbcType=VARCHAR},
	
	
		productSize = #{item.productsize,jdbcType=VARCHAR},
	
	
		price = #{item.price,jdbcType=DECIMAL},
	
		modifyTime = NOW(),
	
		modifyBy = #{item.modifyby,jdbcType=VARCHAR},
	

	where 1=1
	
	and	orderId = #{item.orderid,jdbcType=VARCHAR}
	
	
	and	storeId = #{item.storeid,jdbcType=VARCHAR}
	
	
	and	handtag = #{item.handtag,jdbcType=INTEGER}
	
	
	and	productId = #{item.productid,jdbcType=VARCHAR}
	
	
	and	productName = #{item.productname,jdbcType=VARCHAR}
	
	
	and	productUnit = #{item.productunit,jdbcType=VARCHAR}
	
	
	and	productColor = #{item.productcolor,jdbcType=VARCHAR}
	
	
	and	productSize = #{item.productsize,jdbcType=VARCHAR}

update ss_member_stodetail

	
		orderId = #{item.orderid,jdbcType=VARCHAR},
	
	
		storeId = #{item.storeid,jdbcType=VARCHAR},
	
	
		handtag = #{item.handtag,jdbcType=INTEGER},
	
	
		productId = #{item.productid,jdbcType=VARCHAR},
	
	
		productName = #{item.productname,jdbcType=VARCHAR},
	
	
		productUnit = #{item.productunit,jdbcType=VARCHAR},
	
	
		productColor = #{item.productcolor,jdbcType=VARCHAR},
	
	
		productSize = #{item.productsize,jdbcType=VARCHAR},
	
	
		price = #{price,jdbcType=DECIMAL},
	
		modifyTime = NOW(),
	
		modifyBy = #{item.modifyby,jdbcType=VARCHAR},
	

where 1=1
	
	and	orderId = #{item.orderid,jdbcType=VARCHAR}
	
	
	and	storeId = #{item.storeid,jdbcType=VARCHAR}
	
	
	and	handtag = #{item.handtag,jdbcType=INTEGER}
	
	
	and	productId = #{item.productid,jdbcType=VARCHAR}
	
	
	and	productName = #{item.productname,jdbcType=VARCHAR}
	
	
	and	productUnit = #{item.productunit,jdbcType=VARCHAR}
	
	
	and	productColor = #{item.productcolor,jdbcType=VARCHAR}
	
	
